import React, { useState } from 'react'

import './LoginSignup.css'
import user_icon from '../assets/person.png'
import email_icon from '../assets/email.png'
import password_icon from '../assets/password.png'
import { Link,} from 'react-router-dom'
import Chatabot from '../chatbot/Chatabot'



const MyForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    // ... other form fields
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      const response = await fetch('http://127.0.0.1:8000/views', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });
      
      // Handle response as needed
    } catch (error) {
      console.error('Error submitting form:', error);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

 
  
  
  

  return (
    <>


<div className='login1'>
<Chatabot/>
  <div className='containerL'>
    <div className="headerL">
      <div className="textL">{action}</div>
      <div className="underlineL"></div>
    </div>

<form action="/home"onSubmit={handlesubmit}>
  
    <div className="inputs">
      {action === "Login" ? <div></div> : <div className="input">
        <img src={user_icon} alt="" />
        <input type="text" placeholder='Name' name='name'value="formData.name" />
      </div>}

      <div className="input">
        <img src={email_icon} alt="" />
        <input type="email" placeholder='Email Id' name='email' value="formData.input" />
      </div>

      <div className="input">
        <img src={password_icon} alt="" />
        <input type="password" placeholder='Password' name='password' value="formData.input" />
      </div>
    </div>

    {action === "Sign Up" ? <div></div> : <div className="forget-password">Lost Password? <span>Click Here!</span></div>}
    <div className="submit-container">
      <div className={action === "Login" ? "submit gray" : "submit"} onClick={() => { setAction("Sign Up") }}> Sign Up</div>
      <div className={action === "Sign Up" ? "submit gray" : "submit"} onClick={() => { setAction("Login") }}>Login</div>
    </div>

    <div className='sub'>
      <Link to='/home'><button type='submit'>Submit</button></Link>
    </div>


</form>
  </div>
  

</div>



    </>
  )
}


export default LoginSignup
